import React, { useState } from "react";

const SignInForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Username:", username);
    console.log("Password:", password);
    fetch(`http://localhost:8000/api/employees?username=${username}&password=${password}`)
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched employees:", data);
        //save ID and role
        //use role to display correct page
      })
      .catch((error) => {
        console.error("Error fetching employees:", error);
      });
  };

  const handleForgotPassword = () => {
    alert("Redirect to forgot password page.");
    // Or navigate to a real route if using React Router
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded shadow-md w-full max-w-sm">
        <h2 className="text-2xl font-bold mb-6 text-center">Employee Portal Login</h2>
        
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full border border-gray-300 p-2 rounded"
            placeholder="Enter your username"
            required
          />
        </div>

        <div className="mb-6">
          <label className="block text-gray-700 mb-2">Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full border border-gray-300 p-2 rounded"
            placeholder="Enter your password"
            required
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
        >
          Submit
        </button>

        <div className="mt-4 text-center">
          <button
            type="button"
            className="text-blue-500 hover:underline text-sm"
            onClick={handleForgotPassword}
          >
            Forgot password?
          </button>
        </div>
      </form>
    </div>
  );
};

export default SignInForm;
