import React, { useState } from "react";

function SignInForm() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");

    if (!email || !password) {
      setError("Please enter both email and password.");
      return;
    }

    // Replace with real authentication logic
    if (email === "admin@quadrant.com" && password === "password123") {
      alert("Signed in successfully!");
    } else {
      setError("Invalid email or password.");
    }
  };

  return (
    <div className="min-h-screen bg-[#efeafe] flex flex-col items-center">
      {/* Header section */}
      <div className="w-full bg-[#c2aedc] shadow-md">
        <div className="max-w-5xl mx-auto py-5 flex items-center justify-center">
          {/* Replace with actual image if needed */}
          <img
            src="frontend\src\Images\quadrant-logo-1.png"
            alt="Quadrant Technologies"
            className="h-20 object-contain"
          />
        </div>
        <div className="h-4 bg-gray-600" />
      </div>

      {/* Sign-in form */}
      <div className="flex flex-col mt-10 items-center w-full max-w-sm">
        <form
          onSubmit={handleSubmit}
          className="bg-white p-8 rounded shadow-md w-full"
        >
          <h2 className="text-lg font-semibold mb-4 text-center">Sign In</h2>
          {error && (
            <p className="text-red-500 text-sm mb-4 text-center">{error}</p>
          )}

          <label className="block mb-2 text-sm font-medium">Email</label>
          <input
            type="email"
            placeholder="Email"
            className="w-full px-3 py-2 mb-4 border rounded"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <label className="block mb-2 text-sm font-medium">Password</label>
          <input
            type="password"
            placeholder="Password"
            className="w-full px-3 py-2 mb-4 border rounded"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          <button
            type="submit"
            className="w-full py-2 bg-black text-white rounded hover:bg-gray-800"
          >
            Sign In
          </button>

          <div className="mt-4 text-center">
            <a href="#" className="text-sm underline text-gray-700">
              Forgot password?
            </a>
          </div>
        </form>
      </div>
    </div>
  );
}

export default SignInForm;
