import React, { useState } from "react";

function SignInForm() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");

    if (!email || !password) {
      setError("Please enter both email and password.");
      return;
    }

    // Example validation logic
    if (email === "admin@quadrant.com" && password === "password123") {
      alert("Successfully signed in!");
    } else {
      setError("Invalid email or password.");
    }
  };

<div className="w-[1366px] h-[700px] relative bg-indigo-50">
  <div className="min-w-80 p-6 left-[501px] top-[287px] absolute bg-Background-Default-Default rounded-lg outline outline-1 outline-offset-[-1px] outline-Border-Default-Default inline-flex flex-col justify-start items-start gap-6">
    <div data-has-description="false" data-has-error="false" data-has-label="true" data-state="Default" data-value-type="Placeholder" className="self-stretch flex flex-col justify-start items-start gap-2">
      <div className="self-stretch justify-start text-Text-Default-Default text-base font-normal font-['Inter'] leading-snug">Email</div>
      <div className="self-stretch min-w-60 px-4 py-3 bg-Background-Default-Default rounded-lg outline outline-1 outline-offset-[-0.50px] outline-Border-Default-Default inline-flex justify-start items-center overflow-hidden">
        <div className="flex-1 justify-start text-Text-Default-Tertiary text-base font-normal font-['Inter'] leading-none">Email</div>
      </div>
    </div>
    <div data-has-description="false" data-has-error="false" data-has-label="true" data-state="Default" data-value-type="Placeholder" className="self-stretch flex flex-col justify-start items-start gap-2">
      <div className="self-stretch justify-start text-Text-Default-Default text-base font-normal font-['Inter'] leading-snug">Password</div>
      <div className="self-stretch min-w-60 px-4 py-3 bg-Background-Default-Default rounded-lg outline outline-1 outline-offset-[-0.50px] outline-Border-Default-Default inline-flex justify-start items-center overflow-hidden">
        <div className="flex-1 justify-start text-Text-Default-Tertiary text-base font-normal font-['Inter'] leading-none">Password</div>
      </div>
    </div>
    <div data-align="Justify" data-button-end="true" data-button-start="false" className="self-stretch inline-flex justify-start items-center gap-4">
      <div data-has-icon-end="false" data-has-icon-start="false" data-size="Medium" data-state="Default" data-variant="Primary" className="flex-1 p-3 bg-Background-Brand-Default rounded-lg outline outline-1 outline-offset-[-1px] outline-Border-Brand-Default flex justify-center items-center gap-2 overflow-hidden">
        <div className="justify-start text-Text-Brand-On-Brand text-base font-normal font-['Inter'] leading-none">Sign In</div>
      </div>
    </div>
    <div className="self-stretch inline-flex justify-start items-start">
      <div className="justify-start text-Text-Default-Default text-base font-normal font-['Inter'] underline leading-snug">Forgot password?</div>
    </div>
  </div>
  <div className="w-[1366px] h-60 left-0 top-0 absolute bg-neutral-500" />
  <div className="w-[1366px] h-52 left-0 top-0 absolute bg-slate-300" />
  <img className="w-[608px] h-36 left-[365px] top-[30px] absolute shadow-[0px_4px_4px_0px_rgba(0,0,0,0.25)]" src="https://placehold.co/608x142" />
  <div className="w-[1366px] h-16 left-0 top-[636px] absolute bg-violet-900" />
</div>
}

export default SignInForm;
