import React, { useState } from "react";

function SignInForm() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");

    if (!email || !password) {
      setError("Please enter both email and password.");
      return;
    }

    // Example validation logic
    if (email === "admin@quadrant.com" && password === "password123") {
      alert("Successfully signed in!");
    } else {
      setError("Invalid email or password.");
    }
  };

  return (
    <div className="min-h-screen bg-[#efeafe] flex flex-col">
      {/* Header with logo */}
      <div className="bg-[#c2aedc] flex justify-center py-4 shadow-md">
        <img
          src="/logo.png" // Replace with your actual logo path
          alt="Quadrant Technologies"
          className="h-24"
        />
      </div>
      <div className="h-6 bg-gray-600" />

      {/* Sign-in form */}
      <div className="flex justify-center mt-10">
        <form
          onSubmit={handleSubmit}
          className="bg-white p-6 rounded-md shadow-md w-80"
        >
          <label className="block mb-1 text-sm font-medium text-gray-700">
            Email
          </label>
          <input
            type="email"
            placeholder="Email"
            value={email}
            className="w-full mb-4 px-3 py-2 border border-gray-300 rounded"
            onChange={(e) => setEmail(e.target.value)}
          />

          <label className="block mb-1 text-sm font-medium text-gray-700">
            Password
          </label>
          <input
            type="password"
            placeholder="Password"
            value={password}
            className="w-full mb-4 px-3 py-2 border border-gray-300 rounded"
            onChange={(e) => setPassword(e.target.value)}
          />

          <button
            type="submit"
            className="w-full bg-black text-white py-2 rounded hover:bg-gray-900"
          >
            Sign In
          </button>

          <div className="mt-4 text-center">
            <a
              href="#"
              className="text-sm text-black underline hover:text-gray-800"
            >
              Forgot password?
            </a>
          </div>
        </form>
      </div>
    </div>
  );
}

export default SignInForm;
